INSERT INTO books (id, title, description, long_description, price, cover_image, stripe_price_id, active, created_at, updated_at, "order") VALUES (1, 'Cuaderno de sombra', 'Relatos cortos, densos, escritos para leerse despacio.', 'Una colección de textos breves que exploran el pulso entre luz y silencio. Edición cuidada, ritmo editorial, y una voz directa.', 18, NULL, NULL, TRUE, '2026-01-15 09:44:41', '2026-01-15 15:02:02', 7) ON CONFLICT (id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, long_description = EXCLUDED.long_description, price = EXCLUDED.price, cover_image = EXCLUDED.cover_image, stripe_price_id = EXCLUDED.stripe_price_id, active = EXCLUDED.active, created_at = EXCLUDED.created_at, updated_at = EXCLUDED.updated_at, "order" = EXCLUDED."order";
INSERT INTO books (id, title, description, long_description, price, cover_image, stripe_price_id, active, created_at, updated_at, "order") VALUES (2, 'La ciudad invisible', 'Una novela corta sobre lo que no se nombra.', 'Capítulos breves, escenas nítidas y un tono minimalista. Pensado para lectores que disfrutan de la pausa y la tensión contenida.', 22, NULL, NULL, TRUE, '2026-01-15 09:44:41', '2026-01-15 15:01:59', 8) ON CONFLICT (id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, long_description = EXCLUDED.long_description, price = EXCLUDED.price, cover_image = EXCLUDED.cover_image, stripe_price_id = EXCLUDED.stripe_price_id, active = EXCLUDED.active, created_at = EXCLUDED.created_at, updated_at = EXCLUDED.updated_at, "order" = EXCLUDED."order";
INSERT INTO books (id, title, description, long_description, price, cover_image, stripe_price_id, active, created_at, updated_at, "order") VALUES (3, 'Notas para el fin del día', 'Ensayo breve: escritura, oficio y mirada.', 'Un texto íntimo sobre el proceso creativo. Ideal para quien busca una lectura corta y profunda.', 14, NULL, NULL, FALSE, '2026-01-15 09:44:41', '2026-01-15 15:01:55', 9) ON CONFLICT (id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, long_description = EXCLUDED.long_description, price = EXCLUDED.price, cover_image = EXCLUDED.cover_image, stripe_price_id = EXCLUDED.stripe_price_id, active = EXCLUDED.active, created_at = EXCLUDED.created_at, updated_at = EXCLUDED.updated_at, "order" = EXCLUDED."order";
INSERT INTO books (id, title, description, long_description, price, cover_image, stripe_price_id, active, created_at, updated_at, "order") VALUES (4, 'El eco de las horas muertas', 'Una novela que explora la soledad urbana y los encuentros fortuitos que cambian el curso de una vida. Escrita con una prosa precisa y atmosférica.', '<p>En las calles de una ciudad que nunca duerme, un hombre busca respuestas en los espacios vacíos entre el ruido y el silencio. <em>El eco de las horas muertas</em> es una historia sobre la pérdida, la memoria y la posibilidad de encontrar luz en los lugares más oscuros.</p><p>Con un estilo narrativo que combina la precisión del minimalismo con la profundidad emocional, Kevin Perez construye un relato íntimo que habla directamente al corazón del lector. Cada capítulo es una pieza cuidadosamente tallada de un mosaico mayor, donde los personajes se cruzan en momentos que parecen casuales pero que están destinados a transformar sus vidas.</p><p>Una obra para leer en las noches tranquilas, cuando el mundo exterior se desvanece y solo queda el eco de las palabras en la mente.</p>', 24.99, NULL, NULL, TRUE, '2026-01-15 12:29:46', '2026-01-15 15:02:09', 5) ON CONFLICT (id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, long_description = EXCLUDED.long_description, price = EXCLUDED.price, cover_image = EXCLUDED.cover_image, stripe_price_id = EXCLUDED.stripe_price_id, active = EXCLUDED.active, created_at = EXCLUDED.created_at, updated_at = EXCLUDED.updated_at, "order" = EXCLUDED."order";
INSERT INTO books (id, title, description, long_description, price, cover_image, stripe_price_id, active, created_at, updated_at, "order") VALUES (5, 'Fragmentos de un invierno sin nombre', 'Una colección de relatos breves que capturan momentos de introspección y revelación. Cada historia es una ventana a un mundo interior complejo y fascinante.', '<p>En <em>Fragmentos de un invierno sin nombre</em>, Kevin Perez reúne doce relatos que exploran las múltiples capas de la experiencia humana. Desde encuentros casuales en cafeterías hasta reflexiones sobre el paso del tiempo, cada texto es una pequeña joya narrativa.</p><p>Los personajes de estos cuentos son personas comunes enfrentándose a momentos extraordinarios: una conversación que cambia todo, un recuerdo que resurge inesperadamente, una decisión que define un destino. Con una prosa elegante y precisa, Perez demuestra su dominio del relato corto, creando atmósferas densas y memorables en pocas páginas.</p><p>Ideal para lectores que buscan historias que los acompañen durante el día, que puedan leerse de una sentada pero que permanezcan en la memoria mucho después. Cada fragmento es una semilla que germina en la imaginación del lector.</p>', 19.5, NULL, NULL, TRUE, '2026-01-15 12:29:46', '2026-01-15 15:02:05', 6) ON CONFLICT (id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, long_description = EXCLUDED.long_description, price = EXCLUDED.price, cover_image = EXCLUDED.cover_image, stripe_price_id = EXCLUDED.stripe_price_id, active = EXCLUDED.active, created_at = EXCLUDED.created_at, updated_at = EXCLUDED.updated_at, "order" = EXCLUDED."order";
INSERT INTO books (id, title, description, long_description, price, cover_image, stripe_price_id, active, created_at, updated_at, "order") VALUES (6, 'Amor contra todo pronóstico', 'Un chico escribe un diario personal donde cuenta sus vivencias más importantes. Con el tiempo le ocurre algo que hace cambiar el rumbo de su vida y el de las personas que más quiere.', '<p>Un chico escribe un diario personal donde cuenta sus vivencias más importantes. Con el tiempo le ocurre algo que hace cambiar el rumbo de su vida y el de las personas que más quiere. Digamos que es algo totalmente imprevisible, muy difícil de digerir tanto para él como para la gente de su entorno más cercano.</p><p>Es probable que no hay más de diez personas en el mundo que les haya ocurrido lo mismo que a este chico y que estén identificadas mediáticamente. No es habitual contarlo, sino quedárselo para uno mismo por miedo al rechazo o lo que puedan pensar porque rompe con los estereotipos de la sociedad y es muy común sentirse juzgado por tanto es un asusto un tanto delicado ya que crea mucha controversia. Es un tema muy tabú y a las personas que les pasa se pueden sentir juzgadas e incomprendidas.</p><p><strong>Publicación:</strong> 2020<br><strong>Géneros:</strong> Romántico, drama, suspense, erótica, aventuras<br><strong>Páginas:</strong> 200<br><strong>Ediciones:</strong> 5<br><strong>Narrador:</strong> protagonista</p>', 18.95, 'covers/8ZImHPxyBmtpnvxIZN4LLswsRLOklVKgXpe83rJH.png', NULL, TRUE, '2026-01-15 13:02:48', '2026-01-15 15:01:36', 1) ON CONFLICT (id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, long_description = EXCLUDED.long_description, price = EXCLUDED.price, cover_image = EXCLUDED.cover_image, stripe_price_id = EXCLUDED.stripe_price_id, active = EXCLUDED.active, created_at = EXCLUDED.created_at, updated_at = EXCLUDED.updated_at, "order" = EXCLUDED."order";
INSERT INTO books (id, title, description, long_description, price, cover_image, stripe_price_id, active, created_at, updated_at, "order") VALUES (7, 'En la casa', 'La historia va de una chica que encuentra un trabajo de interna veinticuatro horas en una casa en el bosque. Debe cuidar de un señor mayor que tiene demencia.', '<p>La historia va de una chica que encuentra un trabajo de interna veinticuatro horas en una casa en el bosque. Debe cuidar de un señor mayor que tiene demencia. Sus labores son estar al tanto de él y también ocuparse de las tareas del hogar: limpiarle, cocinare…</p><p>A medida que pasan las horas y los días ella va viendo cosas un tanto extrañas en la casa rural y comienza a estar inquieta e insegura ya que es la primera vez que percibe algo así, aunque deberá hacerse fuerte e investigar si la morada guarda algún secreto y si hay personas del pasado que están involucradas con este misterio.</p><p><strong>Publicación:</strong> 2022<br><strong>Géneros:</strong> Novela negra, terror, misterio, suspense<br><strong>Páginas:</strong> 304<br><strong>Ediciones:</strong> 6<br><strong>Narrador:</strong> protagonista y omnisciente</p>', 22.95, 'covers/WvruPDTmHQShvWmd3VwLoSdSXc8kbfEI474V9dUD.png', NULL, TRUE, '2026-01-15 13:02:48', '2026-01-15 15:01:51', 2) ON CONFLICT (id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, long_description = EXCLUDED.long_description, price = EXCLUDED.price, cover_image = EXCLUDED.cover_image, stripe_price_id = EXCLUDED.stripe_price_id, active = EXCLUDED.active, created_at = EXCLUDED.created_at, updated_at = EXCLUDED.updated_at, "order" = EXCLUDED."order";
INSERT INTO books (id, title, description, long_description, price, cover_image, stripe_price_id, active, created_at, updated_at, "order") VALUES (8, 'Territorio Devastado', 'La novela cuenta como una familia de hace 400 años en el siglo XVI viven del campo. Un día por la noche alguien llama a la puerta del hogar de esta gente y ellos se despiertan, se asustan.', '<p>La novela cuenta como una familia de hace 400 años en el siglo XVI viven del campo, tienen un pequeño huertecito donde siembran verduras y viven del burro, la vaca y las gallinas que tiene en la planta baja de su humilde casita de barro.</p><p>Un día por la noche alguien llama a la puerta del hogar de esta gente y ellos se despiertan, se asustan. Bajan a ver de quién se trata temblorosos. Resulta ser un señor mayor que pide auxilio. Es el tabernero del pueblo, un señor mayor que es muy popular en el pueblo. Este está muy grave, tiene heridas por todo el cuerpo. La familia le preguntan qué le ha ocurrido preocupándose por él. Pero el señor no puede hablar y se desmaya.</p><p>Van a buscar al médico y cuando viene le da a la familia unas hierbas medicinales de aquella época. Al señor lo tienen en una cama durante unas horas intentando que se restablezca, sin embargo, de repente se escapa en esas condiciones tan desfavorables. ¿Cómo puede ser que alguien que se está muriendo en una cama se haya escapado así sin previo aviso?</p><p><strong>Publicación:</strong> 2025<br><strong>Géneros:</strong> Novela histórica, terror, ciencia ficción, misterio<br><strong>Páginas:</strong> 235<br><strong>Ediciones:</strong> 2<br><strong>Narrador:</strong> cámara</p>', 20.95, NULL, NULL, TRUE, '2026-01-15 13:02:48', '2026-01-15 15:02:17', 10) ON CONFLICT (id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, long_description = EXCLUDED.long_description, price = EXCLUDED.price, cover_image = EXCLUDED.cover_image, stripe_price_id = EXCLUDED.stripe_price_id, active = EXCLUDED.active, created_at = EXCLUDE